// Get the raw output string from the previous node
const rawOutput = items[0].json.output;

// Parse the JSON string inside "output"
const parsed = JSON.parse(rawOutput);

// Separate labels and values
const labels = parsed.labels.map(l => String(l));   // optional: cast to string
const values = parsed.values.map(v => Number(v));   // ensure numbers

return [
  {
    json: {
      labels,
      values
    }
  }
];

const labels = $("Cleaned Data").first().json.labels;
const values = $("Cleaned Data").first().json.values;

const cleanedLabels = labels.map(l => String(l));
const cleanedValues = values.map(v => Number(v));

const configLine = {
  type: 'line',
  data: {
    labels: cleanedLabels,
    datasets: [{
      label: "Amount",
      data: cleanedValues,
      borderWidth: 3,
      pointStyle: "circle"
    }]
  },
  options: {
    scales: {
      y: { beginAtZero: true }
    }
  }
};

const configBar = {
  type: "bar",
  data: {
    labels: cleanedLabels,
    datasets: [{
      label: "Amount",
      data: cleanedValues,
      borderWidth: 3
    }]
  },
  options: {
    scales: {
      y: { beginAtZero: true }
    }
  }
};

const configPie = {
  type: "pie",
  data: {
    labels: cleanedLabels,
    datasets: [{
      label: "Amount",
      data: cleanedValues
    }]
  }
};

const chartLineUrl = `https://quickchart.io/chart?c=${encodeURIComponent(JSON.stringify(configLine))}`;
const chartBarUrl = `https://quickchart.io/chart?c=${encodeURIComponent(JSON.stringify(configBar))}`;
const chartPieUrl = `https://quickchart.io/chart?c=${encodeURIComponent(JSON.stringify(configPie))}`;

return [
  {
    json: {
      ...items[0].json,
      chartLineUrl,
      chartBarUrl,
      chartPieUrl
    }
  }
];
